import React, { Component } from "react";

export default class heartCounter extends Component {
    constructor(props) {
        super(props);

        this.state = {
            heartCounterVal: 0
        }
    }

    addHearts = (prevState) => {
        this.setState({
            heartCounterVal: prevState.value + 1
        }, () => {
            console.log(this.state.heartCounterVal)
        });
    }

    render() {
        return (
            <div onClick={() => this.props.addHearts(this.prevState)}>{this.props.text}</div>
        )
    }
}
