import React, { Component } from "react";
import Button from 'react-bootstrap/Button';

export default class HeartCounter extends Component {
    constructor(props) {
        super(props);

        this.state = {
            heartCounterVal: 0
        }
    }

    addHearts = () => {
        this.setState({
            heartCounterVal: this.state.heartCounterVal + 1
        }, () => {
            console.log(this.state.heartCounterVal)
        });
    }

    render() {
        return (
            <div>
                <Button variant="dark" onClick={() => this.addHearts()}>{this.props.text}</Button>
                <div class="love_counter">Total Love: {this.state.heartCounterVal}</div>
            </div>
        )
    }
}
