import React, { Component } from "react";
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';
import NavDropdown from 'react-bootstrap/NavDropdown';
import './App.css'
import 'bootstrap/dist/css/bootstrap.min.css';
import DisplayList from './DisplayList.js'


export default class FilteredList extends Component {
    constructor(props) {
        super(props);

        this.state = {
            medium: "all"
        }

    }
    onSelectFilterSize = event => {
        this.setState({
            medium: event
        })
    };

    matchesFilterMedium = piece => {
        // all pieces should be shown when no filter is selected
        if (this.state.medium === "all") {
            return true
        }
        else if (this.state.medium === piece.medium) {
            return true
        }
        else {
            return false
        }
    }



    render() {
        return (
            <div>
                <Navbar bg="dark" variant="dark" expand="lg">
                    <Navbar.Brand href="#home">Vivian's Art Porfolio</Navbar.Brand>
                    <Navbar.Toggle aria-controls="basic-navbar-nav" />
                    <Navbar.Collapse id="basic-navbar-nav">
                        <Nav className="mr-auto">
                            <Nav.Link href="#home">Home</Nav.Link>
                            <NavDropdown title="Filter by Awards" id="basic-nav-dropdown">
                                <NavDropdown.Item eventKey="All" onSelect={this.onSelectFilterSize} href="#action/3.1">ALL</NavDropdown.Item>
                                <NavDropdown.Item eventKey="awardWinning" onSelect={this.onSelectFilterSize} href="#action/3.1">award-winning</NavDropdown.Item>
                                <NavDropdown.Item eventKey="noAwards" onSelect={this.onSelectFilterSize} href="#action/3.2">no awards</NavDropdown.Item>
                            </NavDropdown>
                            <NavDropdown title="Filter by Medium" id="basic-nav-dropdown">
                                <NavDropdown.Item eventKey="All" onSelect={this.onSelectFilterSize} href="#action/3.1">ALL</NavDropdown.Item>
                                <NavDropdown.Item eventKey="oilPainting" onSelect={this.onSelectFilterSize} href="#action/3.1">oil painting</NavDropdown.Item>
                                <NavDropdown.Item eventKey="colorPencil" onSelect={this.onSelectFilterSize} href="#action/3.2">color pencil</NavDropdown.Item>
                                <NavDropdown.Item eventKey="ink" onSelect={this.onSelectFilterSize} href="#action/3.3">ink</NavDropdown.Item>
                                <NavDropdown.Item eventKey="waterColor" onSelect={this.onSelectFilterSize} href="#action/3.4">water color</NavDropdown.Item>
                                <NavDropdown.Item eventKey="collage" onSelect={this.onSelectFilterSize} href="#action/3.5">collage</NavDropdown.Item>
                                <NavDropdown.Item eventKey="mixedMedia" onSelect={this.onSelectFilterSize} href="#action/3.6">mixed media</NavDropdown.Item>
                            </NavDropdown>
                            <NavDropdown title="Sort By" id="basic-nav-dropdown">
                                <NavDropdown.Item href="#action/3.1">newest to oldest</NavDropdown.Item>
                                <NavDropdown.Item href="#action/3.2">oldest to newest</NavDropdown.Item>
                            </NavDropdown>
                        </Nav>
                    </Navbar.Collapse>
                </Navbar>
                <DisplayList list={this.props.list.filter(this.matchesFilterMedium)} />
            </div>

        )

    }

}
